import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";
import { handleMemberSignin } from "../../redux/site-member/site-member.actions";

import { Nav, NavOption } from "./navigation.styles";

const Navigation = ({ history, isSignedIn, signOut }) => {
    if (isSignedIn) {
      return (
        <nav className='navigation'>
          <p onClick={() => {signOut(); history.push('/signin')}} className='link'>Sign Out</p>
        </nav>
      );
    } else {
      return (
        <nav className='navigation'>
          <p onClick={() => history.push('/signin')} className='link'>Sign In</p>
          <p onClick={() => history.push('/register')} className='link'>Register</p>
        </nav>
      );
    }
}

const mapStateToProps = (state) => ({
    isSignedIn: state.memberState.memberSignedIn
});

const mapDispatchToProps = (dispatch) => ({
    signOut: () => {dispatch(handleMemberSignin())}
  });

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Navigation));
