import { 
    call, 
    put, 
    takeLatest, 
    all,  
    takeEvery
  } from "redux-saga/effects";

  import { REQUEST_DROP_DOWN_OPTIONS, receiveDropDownOptions } from "./drop-down.actions";

  import { fetchAllAssetsForDropDown } from "../../services/api";


  function* getAssetList(action) {
    try {
      const assetList = yield call(fetchAllAssetsForDropDown, action);
      yield put(receiveDropDownOptions(assetList));
    } catch (error) {
      console.log(error);
    }
  };
  
  export  function* requestAssetListSaga() {
    yield takeEvery(REQUEST_DROP_DOWN_OPTIONS, getAssetList);
  };
  

  export function* dropDownSagas() {
    yield all([
      call(requestAssetListSaga), 
    ])
};
