import { 
  TOGGLE_AVAILABLE_UNITS,
  TOGGLE_QUARANTINED_UNITS 
} from "./pop-ups.actions";  
  
  const INITIAL_STATE = {
    seenAvailableUnits: false,
    seenUnitsInUse:false,
    seenAddUser: false,
    seenAddUnit: false,
    seenQuarantinedUnits : false
  }
  
  // eslint-disable-next-line import/no-anonymous-default-export
  const popUpState = (state = INITIAL_STATE  , { type, popupStatus }) => {
    switch (type) {
      case TOGGLE_AVAILABLE_UNITS:
        return {
          ...state,
          seenAvailableUnits: !state.seenAvailableUnits
      };
      case TOGGLE_QUARANTINED_UNITS:
        return {
          ...state,
          seenQuarantinedUnits: !state.seenQuarantinedUnits
      };
      default:
        return state;
    }
  };
  
  export default popUpState;
