import React, { Component } from "react";
import axios from 'axios';
import './popup.scss' 

export default class AddModel extends Component {
  handleClick = () => {
   this.props.toggleSeenAddModel();
  };
  constructor(props) {
    super(props);
    this.state = {
        model: null,
        selectedFile: null
    }
  }

  onModelChange = (event) => {
    this.setState({model: event.target.value}); 
  } ;

  onFileChange = (event) => {
    this.setState({selectedFile: event.target.files[0]});
  };


  onFileUpload = () => { 
    // Create an object of formData 
    const formData = new FormData(); 
    // Update the formData object 
    formData.append( 
      "myFile", 
      this.state.selectedFile, 
      this.state.selectedFile.name,
      
    ); 
    formData.append('model', this.state.model);
    
    // Request made to the backend api 
    // Send formData object 
    axios.post("http://localhost:3000/upload", formData )
   };
     

render() {
  console.log(this.state.selectedFile)
  return (
   <div className="sub_modal_main">
     <div className="sub_modal_content">
     <span className="close" onClick={this.handleClick}>&times;    </span>
        <div className='container pl1'>
            <h1>Add Model</h1>


            <article className=" center">
              <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                <fieldset className="ba b--transparent ph0 mh0">
                  <div>
                    <label className="db fw6 lh-copy" htmlFor="model">Model Name</label>
                    <input onChange={this.onModelChange} className="pa2 input-reset ba w-100" type="text" name="model"  id="model"/>
                  </div>

                  <div className='mt2'>
                    <label className="db fw6 lh-copy" htmlFor="image">Image</label>
                    <form className='flex flex-column'  encType="multipart/form-data" method="POST"> 
                      <input onChange={this.onFileChange} type="file" name="pic" className="pa2 input-reset ba"/>
                    </form>
                  </div>

                </fieldset>
                <div className="mt3">
                  <input 
                    onClick={this.onFileUpload} 
                    className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                    type = "Submit"
                    defaultValue="Submit"
                  />
                  </div>
              </div>
            </article>
        </div>
    </div>
   </div>
  );
 }
}
