// Used to add a gap between updating the database and requesting the new asset details from the database
function timeout(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
};

export const assetTransaction = async( transaction, fetchNew, assetID, userID = null, quarantineReason) => {
    // Handle trying to check out unit without a username
    if (transaction.name === 'checkOutAsset' && !userID) {
        alert('User is required to check out a unit')
    } else if (transaction.name === 'quarantineAsset' && quarantineReason === '') {
        alert('Reason is required when submitting a unit for quarantine.')
    } else {
        try{
            await transaction(assetID, userID, quarantineReason);
            await timeout(100);
            await fetchNew(assetID);
        } catch(e) {
            console.log(e)
        }
    }
};
