import React, { useState } from "react";
import { connect } from "react-redux";

import { createStructuredSelector } from 'reselect';
import { currentMemberId } from "../../redux/site-member/site-member.selectors";
import { selectAssets } from "../../redux/drop-downs/drop-down.selectors";
import { selectUsers } from "../../redux/drop-downs/drop-down.selectors";

import { toggleActvityTracking } from "../../redux/modal/modal.actions";

import { ModalMain, ModalContent, CloseButton, Header, Article,
  FieldSet, Label, Submit, ModalDate
} from "./modal.styles";

import CustomDropDown from '../custom-drop-down/custom-drop-down.component';

import "react-datepicker/dist/react-datepicker.css";

const ActivityTracking = ( {toggleActvityTracking, assets, users} ) =>{

  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [assetID, setAssetID] = useState('');
  const [userID, setUserID] = useState(null);


  const onAssetIDChange = (event) => {
    setAssetID(event.target.value);
  };

  const onUserIDChange = (event) => {
    setUserID(event.target.value);
  };


  // const inputReset = () => {
  //   userID = null;
  //   asset-id = null;
  //   user-id = null;
  //   document.getElementById('userid').value = '';
  //   document.getElementById('asset-id').value = '';
  //   document.getElementById('user-id').value = '';
  //   document.getElementById('userid').focus();
  // };

  // const fetchReport = (event) => {
  //   console.log(Date.parse(startDate))
  //   console.log(Date.parse(endDate))
  // };
  

  return (
   <ModalMain>
     
      <ModalContent>
        {/* Retrieve updated list of users when modal is closed. */}
        <CloseButton onClick={() => {toggleActvityTracking()}}>&times;</CloseButton>
        <Header>Activity Tracking</Header>
        <Article>
          <button onClick={() => console.log(startDate.getTime(), endDate.getTime())}>LOG IT</button>
          <div action="sign-up_submit" method="get" acceptCharset="utf-8">
            <FieldSet id="sign_up">
                <Label htmlFor="email-address">Start Date</Label>
                <ModalDate 
                  selected={startDate} 
                  timeInputLabel="Time:"
                  dateFormat="MM/dd/yyyy h:mm aa"
                  showTimeInput
                  onChange={date => setStartDate(date)} 
                />
                <Label htmlFor="email-address">End Date</Label>
                <ModalDate
                  selected={endDate}       
                  timeInputLabel="Time:"
                  dateFormat="MM/dd/yyyy h:mm aa"
                  showTimeInput 
                  onChange={date => setEndDate(date)} 
                />
                <Label htmlFor="asset-id">Asset ID</Label>
                <CustomDropDown onChange={onAssetIDChange} type="text" name="asset-id"  id="asset-id" optionList={assets}/>
                <Label htmlFor="user-id">User ID</Label>
                <CustomDropDown onChange={onUserIDChange} type="text" name="user-id"  id="user-id" optionList={users}/>
            </FieldSet>
            <Submit  type = "submit" defaultValue="Submit"/>
          </div>
        </Article>
      </ModalContent>
   </ModalMain>
  );
};

const mapStateToProps = createStructuredSelector({
  memberId: currentMemberId,
  assets: selectAssets,
  users: selectUsers
})

const mapDispatchToProps = (dispatch) => ({
  toggleActvityTracking : () => dispatch(toggleActvityTracking())
});

export default connect(mapStateToProps, mapDispatchToProps)(ActivityTracking);
