import React, { useState, useEffect } from "react";
import {connect} from 'react-redux';
import './popup.scss' 
import AddModel from './add-model.popup'

  // seenAddModel: false
import { toggleAddModel, toggleAddUnit } from "../../redux/pop-ups/pop-ups.actions";
import { addNewAsset } from "../../redux/asset/asset.actions";
import { requestAssetDropDownOptions, requestModelDropDownOptions } from '../../redux/drop-downs/drop-down.actions'


const AddUnit = ( {toggleAddUnit, createNewAsset, toggleAddModel, seenAddModel, getAssetOptions, getModelOptions, models}) => {

  // const [models, setModels] = useState([]);

  // useEffect(() => {
  //     const fetchModels = async() => {
  //     const response = await fetch('http://localhost:3000/models') 
  //     const models = await response.json()
  //     setModels(models)

  //     let modelList = document.getElementById("asset-model")
  //     modelList.length = 1  
  //     // Loop through the models array and populate the asset-model drop-down element. 
  //     for (var i = 0; i < models.length; i++) {
  //       var opt = models[i];
  //       var el = document.createElement("option");
  //       el.textContent = opt.title;
  //       el.value = opt.title;
  //       el.dataset.index = i;
  //       modelList.append(el);
  //     }
  //   }

  //   fetchModels();
  // }, [seenAddModel]);
  // console.log(models)

  useEffect(() => {
    getModelOptions()
  }, [getModelOptions])

  let [assetID,     setAssetID    ] = useState('')
  let [assetName,   setAssetName  ] = useState('')
  let [assetModel,  setAssetModel ] = useState([])
  let [assetSerial, setAssetSerial] = useState('')

  const onAssetidChange = (event) => {
    setAssetID(event.target.value); 
  } ;

  const onAssetNameChange = (event) => {
    setAssetName(event.target.value);
  };

  const onAssetModelChange = (event) => {
    setAssetModel(event.target.value);
  };

 const onAssetSerialChange = (event) => {
    setAssetSerial(event.target.value);
  };

  const inputReset = () => {
    document.getElementById('asset-id').value = ''
    document.getElementById('asset-name').value = ''
    document.getElementById('asset-serial').value = ''
    document.getElementById('asset-model').value = 'Select A Model'
    document.getElementById('asset-id').focus()
    alert('Unit Created!')
  }
  console.log('add unit pop-up mounted')
  
  return (
    
   <div className="modal_main ">
      <div className="modal_content ">
      <span className="close" onClick={toggleAddUnit}>&times;</span>
          <div className='container'>
              <h1>Add Unit</h1>
              <article className="center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset id="sign_up" className="form-container">
                    <div>
                      <label className="form-label" htmlFor="asset-id">Unit ID</label>
                      <input onChange={onAssetidChange} className="form-input" type="text" name="asset-id"  id="asset-id"/>
                    </div>
                    <div className="mt3">
                      <label className="form-label" htmlFor="asset-name">Unit Name</label>
                      <input onChange={onAssetNameChange} className="form-input" type="text" name="asset-name"  id="asset-name"/>
                    </div>
                    
                    <div className="mt3 ">
                      <label className="form-label" htmlFor="asset-model">Model</label>
                        <select id='asset-model' onChange={onAssetModelChange} className="form-input" type="text" name="asset-model">
                          <option>Select A Model</option>
                          {   
                            models ? (
                              models.map(model => (
                              <option key={ model.model_name }> { model.model_name } </option> 
                              ))
                              ):(
                              <option></option>
                              )
                          }
                        </select> 
                        <p id='addmodelbtn' onClick={toggleAddModel} className='pointer'>Add New Model</p>
                    </div>
                    
                    <div className="mt3">
                      <label className="db fw6 lh-copy" htmlFor="asset-serial">Serial Number</label>
                      <input onChange={onAssetSerialChange} className="form-input" type="text" name="asset-serial"  id="asset-serial"/>
                    </div>
                  </fieldset>
                  <div className="mt3">
                    <input 
                      onClick={() => {createNewAsset(assetID, assetName, assetModel, assetSerial); inputReset(); getAssetOptions()}} 
                      className="form-submit" 
                      type = "Submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
      {seenAddModel ? <AddModel/> : null}
   </div>
  );
}

const mapStateToProps = (state) => ({
  models: state.dropDownOptions.modelDropDown,
  seenAddModel : state.popUpState.seenAddModel
});

const mapDispatchToProps = (dispatch) => ({
    createNewAsset  : (assetID, assetName, assetModel, assetSerial) => { dispatch(addNewAsset(assetID, assetName, assetModel, assetSerial)) },
    toggleAddUnit   : () => dispatch(toggleAddUnit()),
    toggleAddModel  : () => dispatch(toggleAddModel()),
    getAssetOptions : () => { dispatch(requestAssetDropDownOptions())},
    getModelOptions : () => { dispatch(requestModelDropDownOptions())}
  })

export default connect(mapStateToProps, mapDispatchToProps)(AddUnit);
