import React, { useState }  from "react";
import {connect} from 'react-redux';
import { useAlert } from 'react-alert';

import axios from 'axios';
import { toggleAddModel } from "../../redux/pop-ups/pop-ups.actions";
import { requestModelDropDownOptions } from '../../redux/drop-downs/drop-down.actions';

import './popup.scss'; 

 const AddModel = ({ toggleAddModel, getModelOptions }) => { 

  const alert = useAlert();

  let [model, setModel] = useState('');
  let [filename, setFilename] = useState(null);

  const onModelChange = (event) => {
    setModel(event.target.value);
  };

  const onFileChange = (event) => {
     setFilename(event.target.files[0]);
  };

  const inputReset = () => {
    model = null;
    filename = null;
    document.getElementById('model').value = '';
    document.getElementById('file').value = '';
  };

  const onFileUpload = () => { 
    if (model && filename){
        // Create an object of formData 
        const formData = new FormData(); 
        // Update the formData object 
        formData.append( 
          "myFile", 
          filename, 
          filename.name,
        ); 
        formData.append('model', model);
        // Request made to the backend api 
        // Send formData object 
        axios.post("http://localhost:3000/upload", formData )
        alert.show(`${model} submitted`, {type: 'success', position:'top center'});
        inputReset();
    } else {
      alert.show('Please ensure that both fields contain information.', {type: 'error', position:'top center'});
    }
   };
     
  return (
   <div className="sub_modal_main">
      <div className="sub_modal_content">
        {/* Retrieve updated list of models when modal is closed. */}
      <span className="close" onClick={() => {toggleAddModel(); getModelOptions();} }>&times;    </span>
          <div className='container pl1'>
              <h1>Add Model</h1>
              <article className=" center">
                <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                  <fieldset className="form-container">
                    <div>
                      <label className="form-label" htmlFor="model">Model Name</label>
                      <input onChange={onModelChange} className="form-input" type="text" name="model"  id="model"/>
                    </div>

                    <div className='mt2'>
                      <label className="form-label" htmlFor="image">Image</label>
                      <form className='flex flex-column'  encType="multipart/form-data" method="POST"> 
                        <input onChange={onFileChange} type="file" name="pic" className="form-input" id="file"/>
                      </form>
                    </div>

                  </fieldset>
                  <div className="mt3">
                    <input 
                      onClick={onFileUpload} 
                      className="form-submit" 
                      type = "Submit"
                      defaultValue="Submit"
                    />
                    </div>
                </div>
              </article>
          </div>
      </div>
   </div>
  );
};



const mapDispatchToProps = (dispatch) => ({
  toggleAddModel: () => dispatch(toggleAddModel()),
  getModelOptions : () => { dispatch(requestModelDropDownOptions())}
});

export default connect(null, mapDispatchToProps)(AddModel);
