import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";
import { handleMemberSignin } from "../../redux/site-member/site-member.actions";

import { NavList, NavOption } from "./navigation.styles";



const Navigation = ({asset, history, isSignedIn, signOut }) => {

    const resetState = () => {
      asset = null
    }

    if (isSignedIn) {
      return (
        <NavList>
          <NavOption onClick={() => { resetState(); signOut(); history.push('/signin');}}>Sign Out</NavOption>
        </NavList>
      );
    } else {
      return (
        <NavList>
          <NavOption onClick={() => history.push('/signin')}>Sign In</NavOption>
          <NavOption onClick={() => history.push('/register')}>Register</NavOption>
        </NavList>
      );
    }
}

const mapStateToProps = (state) => ({
  asset: state.assetData,
  isSignedIn: state.memberState.memberSignedIn
});

const mapDispatchToProps = (dispatch) => ({
    signOut: () => {dispatch(handleMemberSignin())}
  });

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Navigation));
