import { useState, useEffect } from "react";

// loadOptions function will take an array and create an option within out drop down, based off the array's id prop
const loadOptions = (dropDownOptions, listName) => {
    const input = document.getElementById(`${listName}`)

    for (var i = 0; i < dropDownOptions.length; i++) {
        var option = document.createElement("option");
        option.value = dropDownOptions[i].id;
        option.text = dropDownOptions[i].id;
        input.appendChild(option);
    }
};

// pass in our url for the API call and the id of the html list that we want to target in our .jsx file. 
const useFetchDropDown = (url, listId) => {
    const [data] = useState(null);

    useEffect(() => {
        const useFetchDropDown = async () => {
            const res = await fetch(url);
            const dataArray = await res.json();
            loadOptions(dataArray, `${listId}`)
            console.log('Fetch Mounted')
            console.log(dataArray[0].id)
        };

        useFetchDropDown();
    }, [url, listId] );

    return data;
};

export default useFetch;
