// import React, { useEffect, useState } from 'react'
import CustomButton from '../custom-button/custom-button.component'
import { connect } from "react-redux";
import { CurrentAssetStatus} from "../../redux/asset/asset.selectors";

import base64ArrayBuffer from '../../utils/base64'
import { assetTransaction } from "./center.utils";
import image404 from '../../images/404.png'

import { 
    checkInSelectedAssetStart, 
    requestSelectedAssetData,
    checkOutSelectedAssetStart
} from "../../redux/asset/asset.actions";

import './center.styles.scss'


const Center = ({ 
    assetID, assetName, assetSerial, assetImage, assetStatus, checkInAsset, getUpdatedStatus ,checkOutAsset, userID }) => {
    
    return (
        <div className='center-container'>
        {assetID ? (
                <div className='center-inner-container'>

                    <div className='header'>
                        <header className='asset-name'>{assetName}</header>
                        <span className='asset-serial'>{assetSerial}</span>
                    </div>
                    
                    <div className='img-container'>   
                    {/* Here we are using our base64ArrayBuffer function to convert our image array into a viewable picture */}
                        <img className='asset-image' src={`data:image/png;base64,${base64ArrayBuffer(assetImage.data)}`}  alt=''></img> 
                    </div>
                    <p 
                    // Ternary operator is being used to determine the font color for status, based on the String value
                    // <CurrentAssetStatus resulted from the CurrentAssetStatus Selector
                        className={` ${assetStatus === 'Available' ? 'available' : assetStatus.slice(0,9) === 'In Use By' ? 'in-use' : 'quarantine' } asset-status`}>
                        <CurrentAssetStatus/>
                    </p>

                    <div className='button-container'>
                        <CustomButton 
                            isCheckIn 
                            onClick={() => {assetTransaction(checkInAsset, getUpdatedStatus, assetID)}}>
                        CHECK IN
                        </CustomButton>
                        <CustomButton 
                            isCheckOut 
                            onClick={() => {assetStatus.slice(0,9) !== 'In Use By' ? assetTransaction(checkOutAsset, getUpdatedStatus, assetID, userID) : alert('NO')}} >
                        CHECK OUT
                        </CustomButton>
                    </div>

                    <div className='footer'>
                    
                        <span className='instructions'>If there are issues with the asset, please fill out the form below and submit</span>
                        <textarea className='reason-box'></textarea>
                        <CustomButton type='button' >{' '}Submit{' '}</CustomButton>
                    </div>

                </div>
                ):(  
                    <div className='center-inner-container'>
                        <img src={image404} width='450px' height='auto' alt='404 Error Message' ></img>
                    </div>
                )}
                
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    assetID: state.assetData.id,
    assetName: state.assetData.name,
    assetSerial: state.assetData.serial,
    assetImage: state.assetData.image,
    assetStatus:state.assetData.status,
    userID: state.userData.id
});

const mapDispatchToProps = (dispatch) => ({
    checkInAsset: (assetID) => { dispatch(checkInSelectedAssetStart(assetID)) },
    checkOutAsset: (assetID, userID) => { dispatch(checkOutSelectedAssetStart(assetID, userID)) },
    getUpdatedStatus: (assetID) => { dispatch(requestSelectedAssetData(assetID)) }
})

export default connect(mapStateToProps, mapDispatchToProps)(Center);

