import React, { useEffect, useState } from 'react'
import './drop-down.styles.scss'

import useDropDownFetch from '../../effects/use-fetch-drop-down.effect';

import { connect } from "react-redux";
import { requestSelectedAssetData } from "../../redux/asset/asset.actions";

import  selectedListItemID  from "./drop-down.utils";

const AssetDropDown =({ getAssetDetails, dropDowns }) => {

    // useDropDownFetch(`http://localhost:3000/allunits`, 'asset-list');
    const [assets, setAssets] = useState([]);

    useEffect(() => {
        const fetchAssets = async() => {
        const response = await fetch(`http://localhost:3000/allunits`) 
        const assets = await response.json()
        setAssets(assets)
  
        // let modelList = document.getElementById("asset-model")
        // modelList.length = 1  
        // // Loop through the models array and populate the asset-model drop-down element. 
        // for (var i = 0; i < assets.length; i++) {
        //   var opt = assets[i];
        //   var el = document.createElement("option");
        //   el.textContent = opt.title;
        //   el.value = opt.title;
        //   el.dataset.index = i;
        //   modelList.append(el);
        // }
      }
  
      fetchAssets();
    }, []);

    dropDowns = assets

    return(
        <div className='combo-box-container'  >
        <button onClick={() => console.log(dropDowns)}> console log DDS</button>
            <select id="asset-list" className = 'combo-box' onChange={getAssetDetails}>
                <option text=' '/>
            </select>
        </div>
    )
}

const mapStateToProps = (state) => ({
    state: state,
    dropDowns: state.dropDownState
})

const mapDispatchToProps = (dispatch) => ({
    getAssetDetails: () => { dispatch(requestSelectedAssetData(selectedListItemID('asset-list')))}
})

export default connect(mapStateToProps, mapDispatchToProps)(AssetDropDown);
