// Used to add a gap between updating the database and requesting the new asset details from the database
const timeout = (ms) => {
    return new Promise(resolve => setTimeout(resolve, ms));
};

export const assetTransaction = async( transaction, fetchNew, id, uid = null, name, serial, model, comment) => {
    try{
        await transaction(id, uid, name, serial, model, comment);
        await timeout(100);
        await fetchNew(id);
    } catch(error) {
        console.log(error)
    }
};
