import React from 'react'
import './signin.styles.scss'

class SigninPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            signInEmail : '',
            signInPassword: ''
        }
    }

    onEmailChange = (event) => {
        this.setState({signInEmail: event.target.value})
    }

    onPasswordChange = (event) => {
        this.setState({signInPassword: event.target.value})
    }

    onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
                email: this.state.signInEmail,
                password: this.state.signInPassword
            })
        })
        // ! Handling incorrect credentials
        .then(response => response.json()) 
        .then(member => {
            if (member.id) {
                this.props.loadMember(member)
                this.props.onRouteChange('home')
            }
        })
    }

    render() {
        const{ onRouteChange} = this.props;
        
    return (
        <article className="signin-container">
            <main className="signin-inner-container">
                <div className="measure">
                    <fieldset id="sign_up" className="signin-fieldset">
                        <legend className="signin-legend">Sign In</legend>
                        
                        <div className="mt3">
                            <label className="singnin-label" htmlFor="email-address">Email</label>
                            <input
                                className="signin-input" 
                                type="text" 
                                name="email-address"  
                                id="email-address"
                                onChange={this.onEmailChange} 
                            />
                        </div>

                        <div className="mv3">
                            <label className="singnin-label" htmlFor="password">Password</label>
                            <input  
                                className="signin-input" 
                                type="password" 
                                name="password"  
                                id="password"
                                onChange={this.onPasswordChange} 
                                />
                        </div>
                        
                    </fieldset>

                    <div className="">
                        <input 
                            onClick = {this.onSubmitSignIn}
                            className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib tc" 
                            type="submit" 
                            value="Sign in"
                        />
                    </div>
                        
                    <div className="lh-copy mt3">
                        <p 
                            onClick = {() => onRouteChange('register')} 
                            className="f6 link dim black db pointer">Register
                        </p>
                    </div>
                </div>
            </main>
        </article>
    
        );
    }
}


export default SigninPage
