import React, { Component } from "react";
import './popup.css' 
import AddModel from './add-model.popup'

let selectedModel = ''
export default class AddUnit extends Component {
  handleClick = () => {
   this.props.toggle();
  };
  constructor(props) {
    super(props);
    this.state = {
        id: '',
        name: '',
        model: '',
        serial: '',
        models: [],
        seenAddModel: false
    }
    
  }

  seenAddModel = () => {
    this.setState({
      seenAddModel: !this.state.seenAddModel
    });
   };


  componentDidMount()  {
    fetch('http://localhost:3000/models', {
     method: 'get',
     headers: {'Content-Type': 'application/json'},
    })
    .then(data => data.json())
    .then( data => {
    for (var i = 0; i < data.length; i++) {
      var opt = data[i];
      var el = document.createElement("option");
      el.textContent = opt.title;
      el.value = i;
      el.dataset.model = opt.title;
      document.getElementById("combo").append(el);
    }
    document.getElementById("combo").addEventListener('change', function() {
      selectedModel= this.options[this.selectedIndex].dataset.model;
    });
    
  })
  }

  onSubmitUnit = () => {
    fetch('http://localhost:3000/addunit', {
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body : JSON.stringify({
          id: this.state.id,
          name: this.state.name,
          model: this.state.model,
          serial: this.state.serial
        })
    })
    .then(data => data.json())
    document.getElementById('unitid').value = ''
    document.getElementById('unitname').value = ''
    document.getElementById('combo').value = ''
    document.getElementById('serial').value = ''
  }

  onUnitidChange = (event) => {
    this.setState({id: event.target.value}); 
  } ;

  onNameChange = (event) => {
    this.setState({name: event.target.value});
  };

  onModelChange = (event) => {
    this.setState({model: selectedModel});
  };

  onSerialChange = (event) => {
    this.setState({serial: event.target.value});
  };

render() {
  return (
    
   <div className="modal_main ">
     <div className="modal_content ">
     <span className="close" onClick={this.handleClick}>&times;</span>
        <div className='container'>
            <h1>Add Unit</h1>
            <article className="center">
              <div action="sign-up_submit" method="get" acceptCharset="utf-8">
                <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                  <div>
                    <label className="db fw6 lh-copy" htmlFor="unitid">Unit ID</label>
                    <input onChange={this.onUnitidChange} className="pa2 input-reset ba" type="text" name="unitid"  id="unitid"/>
                  </div>
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="password">Unit Name</label>
                    <input onChange={this.onNameChange} className="pa2 input-reset ba " type="text" name="unitname"  id="unitname"/>
                  </div>
                  
                  <div className="mt3 ">
                    <label className="db fw6 lh-copy" htmlFor="model">Model</label>
                      <select id='combo' onChange={this.onModelChange} className="pa2 input-reset ba " type="text" name="model">
                        <option>Select A Model</option>
                      </select> 
                      <p id='addmodelbtn' onClick={this.seenAddModel} className='pointer'>Add New Model</p>
                  </div>
                  
                  <div className="mt3">
                    <label className="db fw6 lh-copy" htmlFor="serial">Serial Number</label>
                    <input onChange={this.onSerialChange} className="pa2 input-reset ba " type="text" name="serial"  id="serial"/>
                  </div>
                </fieldset>
                <div className="mt3">
                  <input 
                    onClick={this.onSubmitUnit} 
                    className="b ph3 pv2 input-reset ba b--black grow pointer f6" 
                    type = "Submit"
                    defaultValue="Submit"
                  />
                  </div>
              </div>
            </article>
        </div>
    </div>
    {this.state.seenAddModel ? <AddModel toggle={this.seenAddModel} /> : null}
   </div>
  );
 }
}
