import React, {useEffect, useState} from "react";
import axios from "axios";
import { connect } from "react-redux";
import { toggleQuarantinedUnits } from "../../redux/pop-ups/pop-ups.actions";

import Loader from 'react-loader-spinner'
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css"
import './popup.scss'

const QuarantinedUnits = ({ toggleQuarantinedUnits, memberId }) => {

    const [data, setData] = useState();
 
    useEffect(() => {
        const fetchData = async () => {
          const result = await axios({
              method: 'post',
              url:'http://localhost:3000/quarantinedassets',
              data: {
                memberId: memberId
              }
            }
          );
     
          setData(result.data);
        };
     
        fetchData(memberId);
      }, [memberId]);

  return (
   <div className="modal_main">
        <div className="modal_report_content">
            <span className="close" onClick={toggleQuarantinedUnits}>&times;    </span>
            <div className='container'>
            <h1>Quarantined Units</h1>
                <div className=" pb4 ">
                    <div className="overflow-auto">
                    {data ?
                        <table className="f6 w-100 mw8 tc" cellSpacing="0">
                        <thead>
                            <tr className="stripe-dark" >
                                <th className="fw6  pa3 bg-white">Unit ID</th>
                                <th className="fw6  pa3 bg-white">Unit Name</th>
                                <th className="fw6  pa3 bg-white">Model</th>
                                <th className="fw6  pa3 bg-white">Serial</th> 
                                <th className="fw6  pa3 bg-white">Comments</th> 
                            </tr>
                        </thead>
                        <tbody className="lh-copy">
                            {data.map(unit => (
                                <tr key={unit.id} className="stripe-dark">
                                <td>{unit.id}</td>
                                <td >{unit.name}</td>
                                <td >{unit.model}</td>
                                <td >{unit.serial}</td>
                                <td >{unit.comments}</td>
                                </tr>
                            ))}
                        </tbody>
                        </table>
                        : <Loader 
                                type="Puff"
                                color="#966eff"
                                height={70}
                                width={70}
                                className='loader'    
                                />}
                    </div>
                </div>
            </div>
        </div>
   </div>
  );
}

const mapStateToProps = (state) => ({
    memberId: state.memberState.memberId,
    seenQuarantinedUnits: state.popUpState.seenQuarantinedUnits
});

const mapDispatchToProps = dispatch => ({
    toggleQuarantinedUnits: () => dispatch(toggleQuarantinedUnits())
  });


export default connect(mapStateToProps, mapDispatchToProps)(QuarantinedUnits);
