import { TOGGLE_MEMBER_SIGN_IN } from "./site-member.actions";

const INITIAL_STATE = { 
    memberSignedIn : false 
};

const memberState = (state = INITIAL_STATE, { type }) => {
    switch (type) {
    case TOGGLE_MEMBER_SIGN_IN:
        return { 
            memberSignedIn : !state.memberSignedIn
        };
    default:
        return state 
    }
};

export default memberState;
