export const fetchSelectedUserData = async(userID) => {
    const settings = { 
        method:'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            id:userID
        })
    };
    try{
        const response = await fetch(`http://localhost:3000/user`, settings)
        const user = await response.json();
        return user
    } catch(error) {
        console.log(error)
    }
}

