import React from 'react'
import './drop-down.styles.scss'

import useFetchDropDown from '../../effects/use-fetch-drop-down.effect';

import { connect } from "react-redux";
import { requestSelectedUserData } from "../../redux/user/user.actions"

import  selectedListItemID  from "./drop-down.utils";

const UserDropDown =({ getUserDetails }) => {

    useFetchDropDown(`http://localhost:3000/users`, 'user-list');

    return(
        <div className='combo-box-container'  >
            <select id="user-list" className = 'combo-box' onChange={ getUserDetails }>
                <option text=' '/>
            </select>
        </div>
    )
}

const mapStateToProps = (state) => ({
    state:state
});

const mapDispatchToProps = (dispatch) => ({
    getUserDetails: () => { dispatch( requestSelectedUserData ( selectedListItemID('user-list') ) )}
});

export default connect(mapStateToProps, mapDispatchToProps)(UserDropDown);
